mapper.select:
SELECT *
  FROM (
    SELECT a.*, ROWNUM rn
      FROM (
        SELECT * FROM POSTS

        ORDER BY CREATED_AT DESC
      ) a
     WHERE ROWNUM <= #{offset} + #{limit}
  )
 WHERE rn > #{offset}

mapper.update:
UPDATE POSTS
   SET TITLE   = #{title},
       CONTENT = #{content}
 WHERE ID = #{id}
   AND LOWER(AUTHOR) = LOWER(#{author})

mapper.delete:
DELETE FROM POSTS
 WHERE ID = #{id}
   AND LOWER(AUTHOR) = LOWER(#{author})
